import React, { useState } from 'react';
import patientService from '../services/patientService';

const PatientForm = ({ onPatientAdded }) => {
    const [patient, setPatient] = useState({ name: '', status: '', diagnosis: '' });
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleChange = (e) => {
        setPatient({ ...patient, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);
        try {
            const response = await patientService.createPatient(patient);
            if (response && response.message === "Patient was created.") {
                setSuccessMessage("Data Pasien Berhasil Ditambahkan");
                setPatient({ name: '', status: '', diagnosis: '' });
                onPatientAdded(); // Panggil fungsi untuk refresh data
            } else {
                setError("Data Pasien Gagal Ditambahkan: " + (response ? response.message : "Respon tidak valid"));
            }
        } catch (error) {
            console.error("Error creating patient:", error);
            setError("Terjadi kesalahan jaringan atau server.");
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            {error && <div className="text-red-500">{error}</div>}
            {successMessage && <div className="text-green-500">{successMessage}</div>}
            <input type="text" name="name" placeholder="Nama" value={patient.name} onChange={handleChange} className="border p-2 w-full" required /> {/* Menambahkan required */}
            <input type="text" name="status" placeholder="Status" value={patient.status} onChange={handleChange} className="border p-2 w-full" required />
            <input type="text" name="diagnosis" placeholder="Diagnosa" value={patient.diagnosis} onChange={handleChange} className="border p-2 w-full" required />
            <button type="submit" className="bg-blue-500 text-white p-2 rounded">Simpan</button>
        </form>
    );
};

export default PatientForm;
