import React, { useState } from "react";
import patientService from "../services/patientService";

const PatientForm = ({ onPatientAdded }) => {
	const [patient, setPatient] = useState({
		name: "",
		status: "",
		diagnosis: "",
	});
	const [error, setError] = useState(null);
	const [successMessage, setSuccessMessage] = useState(null);

	const handleChange = (e) => {
		setPatient({ ...patient, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		setError(null);
		setSuccessMessage(null);
		try {
			const response = await patientService.createPatient(patient);
			if (response && response.message === "Patient was created.") {
				setSuccessMessage("Data Pasien Berhasil Ditambahkan");
				setPatient({ name: "", status: "", diagnosis: "" });
				onPatientAdded();
			} else {
				setError(
					"Data Pasien Gagal Ditambahkan: " +
						(response ? response.message : "Respon tidak valid")
				);
			}
		} catch (error) {
			setError("Terjadi kesalahan jaringan atau server.");
		}
	};

	return (
    <form
        onSubmit={handleSubmit}
        className="space-y-4 bg-green-50 p-6 rounded-lg shadow-lg border border-green-200"
    >
        {error && <div className="text-red-500 text-center">{error}</div>}
        {successMessage && <div className="text-green-500 text-center">{successMessage}</div>}
        <input
            type="text"
            name="name"
            placeholder="Nama"
            value={patient.name}
            onChange={handleChange}
            className="border border-green-400 p-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-green-500"
            required
        />
        <input
            type="text"
            name="status"
            placeholder="Status"
            value={patient.status}
            onChange={handleChange}
            className="border border-green-400 p-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-green-500"
            required
        />
        <input
            type="text"
            name="diagnosis"
            placeholder="Diagnosa"
            value={patient.diagnosis}
            onChange={handleChange}
            className="border border-green-400 p-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-green-500"
            required
        />
        <button
            type="submit"
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded transition duration-200 w-full"
        >
            Simpan
        </button>
    </form>
);
};

export default PatientForm;
