import React, { useState, useEffect } from 'react';
import PatientForm from './components/PatientForm';
import PatientList from './components/PatientList';
import patientService from './services/patientService';
import PatientEditForm from './PatientEditForm';


function App() {
    const [patients, setPatients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPatients = async () => {
        setLoading(true); // Set loading sebelum fetch data
        setError(null); // Reset error
        try {
            const data = await patientService.getPatients();
            setPatients(data);
        } catch (err) {
            setError(err.message || "Gagal mengambil data pasien.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPatients();
    }, []);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Aplikasi Input Data Pasien</h1>
            <PatientForm onPatientAdded={fetchPatients} />
            {loading && <div>Memuat data...</div>}
            {error && <div className="text-red-500">{error}</div>}
            {!loading && !error && <PatientList patients={patients} onPatientDeleted={fetchPatients} />} {/* Kirim patients dan onPatientDeleted sebagai props */}
        </div>
    );
}

export default App;
