import React, { useState, useEffect } from 'react';
import PatientForm from './components/PatientForm';
import PatientList from './components/PatientList';
import patientService from './services/patientService';

function App() {
    const [patients, setPatients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPatients = async () => {
        setLoading(true); // Set loading sebelum fetch data
        setError(null); // Reset error
        try {
            const data = await patientService.getPatients();
            setPatients(data);
        } catch (err) {
            setError(err.message || "Gagal mengambil data pasien.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPatients();
    }, []);

    return (
        <form
            onSubmit={handleSubmit}
            className="space-y-6 bg-gradient-to-r from-blue-50 to-green-50 shadow-lg rounded-lg p-8 max-w-lg mx-auto border border-green-200"
        >
            <h2 className="text-2xl font-bold text-center mb-6 text-green-700">
                🏥 Form Input Data Pasien
            </h2>
            {error && (
                <div className="text-red-500 bg-red-50 border border-red-200 rounded-lg p-3 text-center">
                    {error}
                </div>
            )}
            {successMessage && (
                <div className="text-green-700 bg-green-50 border border-green-200 rounded-lg p-3 text-center">
                    {successMessage}
                </div>
            )}
    
            <div>
                <label
                    htmlFor="name"
                    className="block text-green-700 font-medium mb-2"
                >
                    Nama Pasien
                </label>
                <input
                    id="name"
                    type="text"
                    name="name"
                    placeholder="Masukkan nama pasien"
                    value={patient.name}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-green-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <label
                    htmlFor="status"
                    className="block text-green-700 font-medium mb-2"
                >
                    Status Pasien
                </label>
                <input
                    id="status"
                    type="text"
                    name="status"
                    placeholder="Masukkan status pasien"
                    value={patient.status}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-green-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <label
                    htmlFor="diagnosis"
                    className="block text-green-700 font-medium mb-2"
                >
                    Diagnosa Pasien
                </label>
                <input
                    id="diagnosis"
                    type="text"
                    name="diagnosis"
                    placeholder="Masukkan diagnosa pasien"
                    value={patient.diagnosis}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-green-500 focus:outline-none"
                    required
                />
            </div>
            <div className="text-center">
                <button
                    type="submit"
                    className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded-lg transition-all shadow-md"
                >
                    Simpan
                </button>
            </div>
        </form>
    );
    
}

export default App;
