import React, { useState } from 'react';
import patientService from '../services/patientService';

const PatientForm = ({ onPatientAdded }) => {
    const [patient, setPatient] = useState({ name: '', status: '', diagnosis: '' });
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleChange = (e) => {
        setPatient({ ...patient, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);
        try {
            const response = await patientService.createPatient(patient);
            if (response && response.message === "Patient was created.") {
                setSuccessMessage("‚úÖ Data Pasien Berhasil Ditambahkan");
                setPatient({ name: '', status: '', diagnosis: '' });
                onPatientAdded(); // Panggil fungsi untuk refresh data
            } else {
                setError("‚ùå Data Pasien Gagal Ditambahkan: " + (response ? response.message : "Respon tidak valid"));
            }
        } catch (error) {
            console.error("Error creating patient:", error);
            setError("‚ùå Terjadi kesalahan jaringan atau server.");
        }
    };

    return (
        <form
            onSubmit={handleSubmit}
            className="space-y-6 bg-white shadow-lg rounded-lg p-6 max-w-lg mx-auto"
        >
            <div className="text-center bg-gradient-to-r from-blue-500 to-green-500 text-white py-4 rounded-md">
                <h2 className="text-xl font-bold">Form Input Data Pasien üè•</h2>
            </div>

            {error && (
                <div
                    className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative"
                    role="alert"
                >
                    <strong className="font-bold">Error:</strong>
                    <span className="block sm:inline ml-2">{error}</span>
                </div>
            )}

            {successMessage && (
                <div
                    className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative"
                    role="alert"
                >
                    <strong className="font-bold">Success:</strong>
                    <span className="block sm:inline ml-2">{successMessage}</span>
                </div>
            )}

            <div>
                <input
                    type="text"
                    name="name"
                    placeholder="Nama Pasien"
                    value={patient.name}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    name="status"
                    placeholder="Status Pasien"
                    value={patient.status}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <textarea
                    name="diagnosis"
                    placeholder="Diagnosa Pasien"
                    value={patient.diagnosis}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none resize-none"
                    rows="4"
                    required
                ></textarea>
            </div>
            <div className="text-center">
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg transition-all"
                >
                    Simpan
                </button>
            </div>
        </form>
    );
};

export default PatientForm;
