import React, { useState } from 'react';
import patientService from '../services/patientService';

const PatientForm = ({ onPatientAdded }) => {
    const [patient, setPatient] = useState({ name: '', status: '', diagnosis: '' });
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleChange = (e) => {
        setPatient({ ...patient, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);
        try {
            const response = await patientService.createPatient(patient);
            if (response && response.message === "Patient was created.") {
                setSuccessMessage("Data Pasien Berhasil Ditambahkan");
                setPatient({ name: '', status: '', diagnosis: '' });
                onPatientAdded(); // Panggil fungsi untuk refresh data
            } else {
                setError("Data Pasien Gagal Ditambahkan: " + (response ? response.message : "Respon tidak valid"));
            }
        } catch (error) {
            console.error("Error creating patient:", error);
            setError("Terjadi kesalahan jaringan atau server.");
        }
    };

    return (
        <form
            onSubmit={handleSubmit}
            className="space-y-6 bg-white shadow-lg rounded-lg p-6 max-w-md mx-auto"
        >
            <h2 className="text-xl font-bold text-center mb-4">Form Input Data Pasien</h2>
            {error && <div className="text-red-500 text-center">{error}</div>}
            {successMessage && <div className="text-green-500 text-center">{successMessage}</div>}
            
            <div>
                <input
                    type="text"
                    name="name"
                    placeholder="Nama"
                    value={patient.name}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    name="status"
                    placeholder="Status"
                    value={patient.status}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    name="diagnosis"
                    placeholder="Diagnosa"
                    value={patient.diagnosis}
                    onChange={handleChange}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    required
                />
            </div>
            <div className="text-center">
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg transition-all"
                >
                    Simpan
                </button>
            </div>
        </form>
    );
    
};

export default PatientForm;
