import React, { useState } from 'react';
import patientService from '../services/patientService';
import PatientEditForm from './PatientEditForm';

const PatientList = ({ patients, onPatientDeleted, onPatientUpdated }) => {
    const [editingPatient, setEditingPatient] = useState(null);

    const handleDelete = async (id) => {
        if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) {
            try {
                await patientService.deletePatient(id);
                onPatientDeleted(); // Panggil fungsi untuk refresh data setelah dihapus
            } catch (err) {
                alert(err.message || "Gagal menghapus data pasien.");
            }
        }
    };

    const PatientEditForm = ({ patient, onCancel, onSave }) => {
        const [name, setName] = useState(patient?.name || "");
        const [status, setStatus] = useState(patient?.status || "");
        const [diagnosis, setDiagnosis] = useState(patient?.diagnosis || "");
    
        const handleSubmit = (e) => {
            e.preventDefault();
            onSave({ ...patient, name, status, diagnosis }); // Kirim data pasien yang diperbarui
        };
        
        return (
            <div>
                <h2 className="text-xl font-bold mb-4">Daftar Pasien</h2>
                {editingPatient ? (
                    <PatientEditForm
                        patient={editingPatient}
                        onCancel={handleCancelEdit}
                        onSave={async (updatedPatient) => {
                            try {
                                await patientService.updatePatient(updatedPatient); // Panggil API update
                                onPatientUpdated(); // Refresh data
                                setEditingPatient(null); // Tutup form edit
                            } catch (err) {
                                alert(err.message || "Gagal memperbarui data pasien.");
                            }
                        }}
                    />
                ) : (
                    <>
                        {patients.length === 0 ? (
                            <p>Tidak ada data pasien.</p>
                        ) : (
                            <table className="w-full border-collapse border border-gray-300">
                                <thead>
                                    <tr className="bg-gray-100">
                                        <th className="border border-gray-300 p-2">Nama</th>
                                        <th className="border border-gray-300 p-2">Status</th>
                                        <th className="border border-gray-300 p-2">Diagnosa</th>
                                        <th className="border border-gray-300 p-2">Aksi</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {patients.map((patient) => (
                                        <tr key={patient.id}>
                                            <td className="border border-gray-300 p-2">{patient.name}</td>
                                            <td className="border border-gray-300 p-2">{patient.status}</td>
                                            <td className="border border-gray-300 p-2">{patient.diagnosis}</td>
                                            <td className="border border-gray-300 p-2">
                                                <button onClick={() => handleDelete(patient.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mr-2">Hapus</button>
                                                <button onClick={() => handleEdit(patient)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded">Edit</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        )}
                    </>
                )}
            </div>
        );
        

        return (
            <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded">
                <div>
                    <label>Nama:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="border rounded p-2 w-full"
                    />
                </div>
                <div>
                    <label>Status:</label>
                    <input
                        type="text"
                        value={status}
                        onChange={(e) => setStatus(e.target.value)}
                        className="border rounded p-2 w-full"
                    />
                </div>
                <div>
                    <label>Diagnosa:</label>
                    <textarea
                        value={diagnosis}
                        onChange={(e) => setDiagnosis(e.target.value)}
                        className="border rounded p-2 w-full"
                    ></textarea>
                </div>
                <div className="mt-4">
                    <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Simpan</button>
                    <button type="button" onClick={onCancel} className="ml-2 bg-gray-500 text-white px-4 py-2 rounded">Batal</button>
                </div>
            </form>
        );
    };

    return (
        <div>
            <h2 className="text-xl font-bold mb-4">Daftar Pasien</h2>
            {patients.length === 0 ? (
                <p>Tidak ada data pasien.</p>
                
            ) : (
                <table className="w-full border-collapse border border-gray-300">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border border-gray-300 p-2">Nama</th>
                            <th className="border border-gray-300 p-2">Status</th>
                            <th className="border border-gray-300 p-2">Diagnosa</th>
                            <th className="border border-gray-300 p-2">Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {patients.map((patient) => (
                            <tr key={patient.id}>
                                <td className="border border-gray-300 p-2">{patient.name}</td>
                                <td className="border border-gray-300 p-2">{patient.status}</td>
                                <td className="border border-gray-300 p-2">{patient.diagnosis}</td>
                                <td className="border border-gray-300 p-2">
                                    <button onClick={() => handleDelete(patient.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mr-2">Hapus</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default PatientList;
