import React, { useState, useEffect } from "react";
import patientService from "../services/patientService";

const PatientEditForm = ({ patient, onSave, onCancel }) => {
	const [editedPatient, setEditedPatient] = useState({ ...patient });
	const [error, setError] = useState(null);

	useEffect(() => {
		setEditedPatient({ ...patient });
	}, [patient]);

	const handleChange = (e) => {
		setEditedPatient({ ...editedPatient, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		setError(null);
		try {
			const response = await patientService.updatePatient(editedPatient);
			if (response && response.message === "Patient was updated.") {
				onSave();
				onCancel();
			} else {
				setError(
					"Gagal memperbarui pasien: " +
						(response?.message || "Terjadi kesalahan")
				);
			}
		} catch (error) {
			setError(
				"Terjadi kesalahan jaringan atau server: " + (error?.message || "")
			);
		}
	};

	return (
		<div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
			<div className="bg-white rounded-lg p-8 max-w-md shadow-lg transition-transform transform-gpu">
				<h2 className="text-xl font-bold mb-4">Edit Pasien</h2>
				{error && <div className="text-red-500 mb-4">{error}</div>}
				<form
					onSubmit={handleSubmit}
					className="space-y-4"
				>
					<input
						type="hidden"
						name="id"
						value={editedPatient.id}
					/>
					<input
						type="text"
						name="name"
						placeholder="Nama"
						value={editedPatient.name}
						onChange={handleChange}
						className="border p-2 w-full rounded"
						required
					/>
					<input
						type="text"
						name="status"
						placeholder="Status"
						value={editedPatient.status}
						onChange={handleChange}
						className="border p-2 w-full rounded"
						required
					/>
					<input
						type="text"
						name="diagnosis"
						placeholder="Diagnosa"
						value={editedPatient.diagnosis}
						onChange={handleChange}
						className="border p-2 w-full rounded"
						required
					/>
					<div className="flex justify-end">
						<button
							type="button"
							onClick={onCancel}
							className="bg-gray-400 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded mr-2"
						>
							Batal
						</button>
						<button
							type="submit"
							className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
						>
							Simpan
						</button>
					</div>
				</form>
			</div>
		</div>
	);
};

export default PatientEditForm;
