import React, { useState } from 'react';
import patientService from '../services/patientService';
import PatientEditForm from './PatientEditForm';

const PatientList = ({ patients, onPatientDeleted, onPatientUpdated }) => {
    const [editingPatient, setEditingPatient] = useState(null);

    const handleDelete = async (id) => {
        if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) {
            try {
                await patientService.deletePatient(id);
                onPatientDeleted(); // Panggil fungsi untuk refresh data setelah dihapus
            } catch (err) {
                alert(err.message || "Gagal menghapus data pasien.");
            }
        }
    };

    const PatientEditForm = ({ patient, onCancel, onSave }) => {
        const [name, setName] = useState(patient?.name || "");
        const [status, setStatus] = useState(patient?.status || "");
        const [diagnosis, setDiagnosis] = useState(patient?.diagnosis || "");
    
        const handleSubmit = (e) => {
            e.preventDefault();
            onSave({ ...patient, name, status, diagnosis }); // Kirim data pasien yang diperbarui
        };
        

        return (
            <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded">
                <div>
                    <label>Nama:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="border rounded p-2 w-full"
                    />
                </div>
                <div>
                    <label>Status:</label>
                    <input
                        type="text"
                        value={status}
                        onChange={(e) => setStatus(e.target.value)}
                        className="border rounded p-2 w-full"
                    />
                </div>
                <div>
                    <label>Diagnosa:</label>
                    <textarea
                        value={diagnosis}
                        onChange={(e) => setDiagnosis(e.target.value)}
                        className="border rounded p-2 w-full"
                    ></textarea>
                </div>
                <div className="mt-4">
                    <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Simpan</button>
                    <button type="button" onClick={onCancel} className="ml-2 bg-gray-500 text-white px-4 py-2 rounded">Batal</button>
                </div>
            </form>
        );
    };

    return (
        <div className="bg-gray-100 min-h-screen p-8 font-sans">
            <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
                <div className="bg-blue-500 text-white py-6 px-6 flex justify-center items-center">
                    <h1 className="text-3xl font-bold tracking-wide">Aplikasi Input Data Pasien</h1>
                </div>
                <div className="p-6">
                    <PatientForm onPatientAdded={fetchPatients} />

                    {loading && (
                        <div className="text-center py-4">
                            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
                            <p className="mt-2 text-gray-500">Memuat data...</p>
                        </div>
                    )}

                    {error && (
                        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
                            <strong className="font-bold">Error!</strong>
                            <span className="block sm:inline">{error}</span>
                        </div>
                    )}

                    {!loading && !error && patients.length > 0 && (
                        <div className="mt-6">
                            <h2 className="text-lg font-semibold mb-4 border-b pb-2">Daftar Pasien</h2>
                            <table className="w-full border-collapse border border-gray-300 rounded-lg shadow-md overflow-hidden">
                                <thead>
                                    <tr className="bg-gray-100 text-left"> {/* Added text-left for left-aligned table headers */}
                                        <th className="p-2">Nama</th>
                                        <th className="p-2">Status</th>
                                        <th className="p-2">Diagnosa</th>
                                        <th className="p-2">Aksi</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {patients.map((patient) => (
                                        <tr key={patient.id} className="hover:bg-gray-200"> {/* Added hover effect for table rows */}
                                            <td className="p-2">{patient.name}</td>
                                            <td className="p-2">{patient.status}</td>
                                            <td className="p-2">{patient.diagnosis}</td>
                                            <td className="p-2">
                                                <button
                                                    onClick={() => handleDelete(patient.id)}
                                                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                                                >
                                                    Hapus
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}

                    {!loading && !error && patients.length === 0 && (
                        <p className="text-center text-gray-500">Tidak ada data pasien.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PatientList;
