import React, { useState, useEffect } from 'react';
import patientService from '../services/patientService';


const PatientList = () => {
    const [patients, setPatients] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    

    useEffect(() => {
        const fetchPatients = async () => {
            try {
                const data = await patientService.getPatients();
                setPatients(data);
            } catch (err) {
                setError(err.message || "Gagal mengambil data pasien."); // Pesan error yang lebih informatif
            } finally {
                setLoading(false);
            }
        };

        fetchPatients();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) { // Konfirmasi sebelum hapus
            try {
                await patientService.deletePatient(id);
                setPatients(patients.filter(patient => patient.id !== id)); // Update state setelah hapus
            } catch (err) {
                setError(err.message || "Gagal menghapus data pasien.");
            }
        }
    };

    if (loading) {
        return <div>Memuat data...</div>;
    }

    if (error) {
        return <div className="text-red-500">{error}</div>; // Menampilkan pesan error
    }

    return (
        <div>
            <h2 className="text-xl font-bold mb-4">Daftar Pasien</h2>
            {patients.length === 0 ? (
                <p>Tidak ada data pasien.</p>
            ) : (
                <table className="w-full border-collapse border border-gray-300">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border border-gray-300 p-2">Nama</th>
                            <th className="border border-gray-300 p-2">Status</th>
                            <th className="border border-gray-300 p-2">Diagnosa</th>
                            <th className="border border-gray-300 p-2">Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {patients.map((patient) => (
                            <tr key={patient.id}>
                                <td className="border border-gray-300 p-2">{patient.name}</td>
                                <td className="border border-gray-300 p-2">{patient.status}</td>
                                <td className="border border-gray-300 p-2">{patient.diagnosis}</td>
                                <td className="border border-gray-300 p-2">
                                    <button onClick={() => handleDelete(patient.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mr-2">Hapus</button>
                                    {/* Tombol Edit akan ditambahkan nanti */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default PatientList;
