import React, { useState, useEffect } from 'react';
import PatientForm from './components/PatientForm';
import PatientList from './components/PatientList';
import patientService from './services/patientService';

function App() {
    const [patients, setPatients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPatients = async () => {
        setLoading(true); // Set loading sebelum fetch data
        setError(null); // Reset error
        try {
            const data = await patientService.getPatients();
            setPatients(data);
        } catch (err) {
            setError(err.message || "Gagal mengambil data pasien.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPatients();
    }, []);

    return (
        <div className="bg-gray-100 min-h-screen p-8">
            <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
                <div className="bg-blue-500 text-white py-4 px-6">
                    <h1 className="text-2xl font-bold">Aplikasi Input Data Pasien</h1>
                </div>
                <div className="p-6">
                    <PatientForm onPatientAdded={fetchPatients} />

                    {loading && (
                        <div className="text-center py-4">
                            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
                            <p className="mt-2 text-gray-500">Memuat data...</p>
                        </div>
                    )}

                    {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
                        <strong className="font-bold">Error!</strong>
                        <span className="block sm:inline">{error}</span>
                    </div>}

                    {!loading && !error && (
                        <div className="mt-6">
                            <h2 className="text-lg font-semibold mb-4 border-b pb-2">Daftar Pasien</h2>
                            <PatientList patients={patients} onPatientDeleted={fetchPatients} />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default App;
