import React, { useState } from "react";
import patientService from "../services/patientService";

const PatientList = ({ patients, onPatientDeleted }) => {
    const [editingPatient, setEditingPatient] = useState(null);

    const handleDelete = async (id) => {
        if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) {
            try {
                await patientService.deletePatient(id);
                onPatientDeleted(); // Refresh data setelah dihapus
            } catch (err) {
                alert(err.message || "Gagal menghapus data pasien.");
            }
        }
    };

    const handleCancelEdit = () => setEditingPatient(null);

    const handleSaveEdit = async (updatedPatient) => {
        try {
            await patientService.updatePatient(updatedPatient.id, updatedPatient);
            onPatientDeleted(); // Refresh data setelah diperbarui
            setEditingPatient(null);
        } catch (err) {
            alert(err.message || "Gagal memperbarui data pasien.");
        }
    };

    return (
        <div className="p-6 bg-white shadow-lg rounded-lg">
            <h2 className="text-2xl font-bold mb-6 border-b pb-2 text-center bg-gradient-to-r from-blue-500 to-green-500 text-white py-4 rounded-t-md">
                üè• Daftar Pasien
            </h2>

            {patients.length === 0 ? (
                <p className="text-gray-500 text-center">Tidak ada data pasien.</p>
            ) : (
                <div className="overflow-x-auto">
                    <table className="w-full border-collapse border border-gray-300 rounded-lg shadow">
                        <thead>
                            <tr className="bg-blue-100 text-gray-700">
                                <th className="border border-gray-300 p-4">Nama</th>
                                <th className="border border-gray-300 p-4">Status</th>
                                <th className="border border-gray-300 p-4">Diagnosa</th>
                                <th className="border border-gray-300 p-4">Aksi</th>
                            </tr>
                        </thead>
                        <tbody>
                            {patients.map((patient, index) => (
                                <tr
                                    key={patient.id}
                                    className={`hover:bg-blue-50 ${
                                        index % 2 === 0 ? "bg-white" : "bg-gray-50"
                                    }`}
                                >
                                    <td className="border border-gray-300 p-4 text-center">
                                        {patient.name}
                                    </td>
                                    <td className="border border-gray-300 p-4 text-center">
                                        {patient.status}
                                    </td>
                                    <td className="border border-gray-300 p-4 text-center">
                                        {patient.diagnosis}
                                    </td>
                                    <td className="border border-gray-300 p-4 text-center space-x-2">
                                        <button
                                            onClick={() => setEditingPatient(patient)}
                                            className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-1 px-4 rounded transition-all"
                                        >
                                            Edit
                                        </button>
                                        <button
                                            onClick={() => handleDelete(patient.id)}
                                            className="bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-4 rounded transition-all"
                                        >
                                            Hapus
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            {editingPatient && (
                <div className="mt-6 p-4 bg-gray-100 rounded-lg shadow-lg">
                    <h3 className="text-lg font-semibold mb-4 text-center">Edit Data Pasien</h3>
                    <PatientEditForm
                        patient={editingPatient}
                        onCancel={handleCancelEdit}
                        onSave={handleSaveEdit}
                    />
                </div>
            )}
        </div>
    );
};

const PatientEditForm = ({ patient, onCancel, onSave }) => {
    const [name, setName] = useState(patient.name);
    const [status, setStatus] = useState(patient.status);
    const [diagnosis, setDiagnosis] = useState(patient.diagnosis);

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave({ id: patient.id, name, status, diagnosis });
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label className="block text-gray-600">Nama:</label>
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                />
            </div>
            <div>
                <label className="block text-gray-600">Status:</label>
                <input
                    type="text"
                    value={status}
                    onChange={(e) => setStatus(e.target.value)}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none"
                />
            </div>
            <div>
                <label className="block text-gray-600">Diagnosa:</label>
                <textarea
                    value={diagnosis}
                    onChange={(e) => setDiagnosis(e.target.value)}
                    className="border border-gray-300 rounded-lg p-3 w-full focus:ring-2 focus:ring-blue-500 focus:outline-none resize-none"
                ></textarea>
            </div>
            <div className="flex justify-end space-x-4">
                <button
                    type="submit"
                    className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg transition-all"
                >
                    Simpan
                </button>
                <button
                    type="button"
                    onClick={onCancel}
                    className="bg-gray-400 hover:bg-gray-500 text-white font-bold py-2 px-4 rounded-lg transition-all"
                >
                    Batal
                </button>
            </div>
        </form>
    );
};

export default PatientList;
