import axios from 'axios';

const API_URL = 'http://localhost/backend/api/patients'; // Sesuaikan dengan URL backend Anda

const getPatients = async () => {
    try {
        const response = await axios.get(`${API_URL}/read.php`);
        return response.data.patients; // Asumsi response.data berupa { patients: [...] }
    } catch (error) {
        throw error;
    }
};

const deletePatient = async (id) => {
    try {
        const response = await axios.delete(`${API_URL}/delete.php?id=${id}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

const createPatient = async (patientData) => {
    try {
      const response = await axios.post(`${API_URL}/create.php`, patientData);
      return response.data;
    } catch (error) {
      throw error;
    }
  };


  const patientService = {
    getPatients,
    createPatient,
    deletePatient,
  };

export default patientService;
