import React, { useState } from 'react';
import patientService from '../services/patientService';

const PatientForm = ({ onPatientAdded }) => {
  const [patient, setPatient] = useState({ name: '', status: '', diagnosis: '' });

  const handleChange = (e) => {
    setPatient({ ...patient, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!patient.name || !patient.status || !patient.diagnosis) {
        alert("Semua field harus diisi!");
        return;
    }

    try {
        const response = await patientService.createPatient(patient);
        if (response && response.message === "Patient was created.") {
            setSuccessMessage("Data Pasien Berhasil Ditambahkan");
            setPatient({ name: '', status: '', diagnosis: '' });
            onPatientAdded(); // Panggil fungsi untuk refresh data
        } else
        await patientService.createPatient(patient);
        alert("Data Pasien Berhasil Ditambahkan");
        setPatient({ name: '', status: '', diagnosis: '' }); // Reset form
    } catch (error) {
        console.error("Error creating patient:", error);
        alert("Data Pasien Gagal Ditambahkan");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input type="text" name="name" placeholder="Nama" value={patient.name} onChange={handleChange} className="border p-2 w-full" />
      <input type="text" name="status" placeholder="Status" value={patient.status} onChange={handleChange} className="border p-2 w-full" />
      <input type="text" name="diagnosis" placeholder="Diagnosa" value={patient.diagnosis} onChange={handleChange} className="border p-2 w-full" />
      <button type="submit" className="bg-blue-500 text-white p-2 rounded">Simpan</button>
    </form>
  );
};

export default PatientForm;
