import React, { useState } from "react";
import patientService from "../services/patientService";

const PatientForm = ({ onPatientAdded }) => {
	const [patient, setPatient] = useState({
		name: "",
		status: "",
		diagnosis: "",
	});
	const [error, setError] = useState(null);
	const [successMessage, setSuccessMessage] = useState(null);

	const handleChange = (e) => {
		setPatient({ ...patient, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		setError(null);
		setSuccessMessage(null);
		try {
			const response = await patientService.createPatient(patient);
			if (response && response.message === "Patient was created.") {
				setSuccessMessage("Data Pasien Berhasil Ditambahkan");
				setPatient({ name: "", status: "", diagnosis: "" });
				onPatientAdded();
			} else {
				setError(
					"Data Pasien Gagal Ditambahkan: " +
						(response ? response.message : "Respon tidak valid")
				);
			}
		} catch (error) {
			setError("Terjadi kesalahan jaringan atau server.");
		}
	};

	return (
		<form
			onSubmit={handleSubmit}
			className="space-y-6 bg-white p-6 rounded-lg shadow-lg"
		>
			{error && (
				<div className="text-red-500 bg-red-50 border border-red-300 p-2 rounded">
					‚ùå {error}
				</div>
			)}
			{successMessage && (
				<div className="text-green-500 bg-green-50 border border-green-300 p-2 rounded">
					‚úÖ {successMessage}
				</div>
			)}
			<input
				type="text"
				name="name"
				placeholder="Nama Pasien"
				value={patient.name}
				onChange={handleChange}
				className="border border-gray-300 p-3 w-full rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
				required
			/>
			<input
				type="text"
				name="status"
				placeholder="Status Pasien"
				value={patient.status}
				onChange={handleChange}
				className="border border-gray-300 p-3 w-full rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
				required
			/>
			<input
				type="text"
				name="diagnosis"
				placeholder="Diagnosa Pasien"
				value={patient.diagnosis}
				onChange={handleChange}
				className="border border-gray-300 p-3 w-full rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
				required
			/>
			<button
				type="submit"
				className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white font-bold py-2 px-6 rounded-lg shadow-md hover:shadow-lg transition duration-300"
			>
				üíæ Simpan
			</button>
		</form>
	);
	
};

export default PatientForm;
